import { getUserById } from '@/api'

export default {
  namespaced: true,
  state: {
    id: undefined,
    name: undefined,
    email: undefined,
    loading: false
  },
  mutations: {
    SET_NAME (state, name) {
      state.name = name
    },
    SET_EMAIL (state, email) {
      state.email = email
    },
    SET_USER (state, user) {
      state.id = user.id
      state.name = user.name
      state.email = user.email
    },
    EMPTY_USER (state) {
      state.id = undefined
      state.name = undefined
      state.email = undefined
      state.loading = false
    },
    LOADING (state, value) {
      state.loading = value
    }
  },
  actions: {
    load ({ commit }, userId) {
      commit('LOADING', true)
      return new Promise(resolve => {
        getUserById(userId)
          .then(response => {
            commit('SET_USER', response.data)
            commit('LOADING', false)
            resolve()
          })
      })
    }
  },
  getters: {
    user: state => {
      return {
        id: state.id,
        name: state.name,
        email: state.email
      }
    },
    loading: state => state.loading
  }
}

